var bbb = new Array();

this.HIGH = 1;
this.LOW = 0;

//A data in
var A0 = 'P8_7';
var A1 = 'P8_8';
var A2 = 'P8_9';
var A3 = 'P8_10';
var A4 = 'P8_11';
var A5 = 'P8_12';
var A6 = 'P8_13';
var A7 = 'P8_14';
var A8 = 'P8_15';
var A9 = 'P8_16';
var A10 = 'P8_17';
var A11 = 'P8_18';
var A12 = 'P8_19';
var APAR = 'P8_26';

//D data out
var D0 = 'P9_11';
var D1 = 'P9_12';
var D2 = 'P9_13';
var D3 = 'P9_14';
var D4 = 'P9_15';
var D5 = 'P9_16';
var D6 = 'P9_21';
var D7 = 'P9_22';
var D8 = 'P9_23';
var D9 = 'P9_24';
var D10 = 'P9_26';
var D11 = 'P9_27';
var D12 = 'P9_30';
var DPAR = 'P9_41';
var ERROR = 'P9_42';

this.pinMode = function(pin, mode) {
}

this.digitalWrite = function(pin, val) {
	bbb[pin] = val;
}

this.digitalRead = function(pin) {
	if(pin==ERROR){
		var f_err = (
		(bbb[A0]&bbb[A1])|
		(bbb[A1]&bbb[A2])|
		(bbb[A2]&bbb[A3])|
		(bbb[A3]&bbb[A4])|
		(bbb[A4]&bbb[A5])|
		(bbb[A5]&bbb[A6])|
		(bbb[A6]&bbb[A7])|
		(bbb[A7]&bbb[A8])|
		(bbb[A8]&bbb[A9])|
		(bbb[A9]&bbb[A10])|
		(bbb[A10]&bbb[A11])|
		(bbb[A11]&bbb[A12])
		);
		var f_par = (bbb[A0]^bbb[A1]^bbb[A2]^bbb[A3]^bbb[A4]^bbb[A5]^bbb[A6]^bbb[A7]^bbb[A8]^bbb[A9]^bbb[A10]^bbb[A11]^bbb[A12]^bbb[APAR]);
		return f_err|(!f_par);
	}
	else return bbb[pin];
}
